/* global console */$(document).ready(function(){$("abbr, .with-tooltip").on("mouseover",function(){var e=$(this).attr("title"),t=$(this).position();$(this).attr("title","").css("position","relative");if($(this).hasClass("top")){$(this).before('<div class="tooltip in top"><div class="tooltip-inner">'+e+'</div><div class="tooltip-arrow"></div></div>');$(".tooltip").css({top:t.top-28+"px",left:t.left+"px"})}else if($(this).hasClass("bottom")){$(this).after('<div class="tooltip in bottom"><div class="tooltip-inner">'+e+'</div><div class="tooltip-arrow"></div></div>');$(".tooltip").css({bottom:t.bottom+28+"px",left:t.left+"px"})}else if($(this).hasClass("right")){$(this).before('<div class="tooltip in right"><div class="tooltip-inner">'+e+'</div><div class="tooltip-arrow"></div></div>');$(".tooltip").css({top:t.top+"px",left:t.left+$(this).width()+"px"})}else if($(this).hasClass("left")){$(this).before('<div class="tooltip in left"><div class="tooltip-inner">'+e+'</div><div class="tooltip-arrow"></div></div>');$(".tooltip").css({top:t.top+"px",left:t.left-($(".tooltip").width()+8)+"px"})}else{$(this).before('<div class="tooltip in"><div class="tooltip-inner">'+e+'</div><div class="tooltip-arrow"></div></div>');$(".tooltip").css({top:t.top-28+"px",left:t.left+"px"})}});$("abbr, .with-tooltip").on("mouseout",function(){$(this).attr("title",$(".tooltip.in .tooltip-inner").html()).css("position","static");$(".tooltip").remove()})});